INSERT INTO educational_content (title, content, category, author, is_featured, sort_order) VALUES
('Bem-vindos à Maçonaria', 'A Maçonaria é uma das mais antigas e respeitadas organizações fraternas do mundo. Fundada em princípios de fraternidade, igualdade e liberdade, ela busca o aperfeiçoamento moral e intelectual de seus membros...', 'introduction', 'Ir∴ João Silva', true, 1),
('História da Nossa Loja', 'Nossa Augusta e Respeitável Loja foi fundada em 1889, sendo uma das mais tradicionais do estado. Ao longo de mais de um século de existência, tem contribuído para o desenvolvimento moral e social da comunidade...', 'introduction', 'Ir∴ Pedro Santos', false, 2),
('Os Três Graus Simbólicos', 'A Maçonaria Simbólica é composta por três graus fundamentais: Aprendiz, Companheiro e Mestre. Cada grau possui seus próprios ensinamentos, símbolos e rituais que contribuem para a formação maçônica...', 'articles', 'Ir∴ Carlos Oliveira', true, 1),
('Simbolismo do Esquadro e Compasso', 'O Esquadro e o Compasso são os símbolos mais reconhecidos da Maçonaria. O Esquadro representa a retidão de caráter e a honestidade, enquanto o Compasso simboliza a moderação e a busca pelo equilíbrio...', 'articles', 'Ir∴ Roberto Lima', false, 2),
('Ritual e Cerimônias', 'Os rituais maçônicos são cerimônias solenes que transmitem os ensinamentos e valores da Ordem. Cada ritual tem um propósito específico e contribui para a formação moral e espiritual do maçom...', 'articles', 'Ir∴ Fernando Costa', false, 3),
('Aprendiz', 'Primeiro grau da Maçonaria Simbólica. O Aprendiz representa aquele que inicia sua jornada de conhecimento e aperfeiçoamento moral.', 'glossary', 'Sistema', false, 1),
('Companheiro', 'Segundo grau da Maçonaria Simbólica. O Companheiro é aquele que já adquiriu conhecimentos básicos e busca aprofundar sua formação.', 'glossary', 'Sistema', false, 2),
('Mestre', 'Terceiro grau da Maçonaria Simbólica. O Mestre é o maçom que possui plenos direitos na Loja e pode participar de todos os trabalhos.', 'glossary', 'Sistema', false, 3),
('Loja', 'Local onde se reúnem os maçons para realizar seus trabalhos. Também se refere ao conjunto de maçons de uma determinada unidade.', 'glossary', 'Sistema', false, 4),
('Grande Oriente', 'Organização que congrega e administra as Lojas Maçônicas de uma determinada jurisdição territorial.', 'glossary', 'Sistema', false, 5),
('O que é necessário para se tornar maçom?', 'Para se tornar maçom, é necessário ser homem livre e de bons costumes, ter pelo menos 18 anos, acreditar em um Ser Supremo e ser indicado por dois maçons da Loja.', 'faq', 'Sistema', false, 1),
('A Maçonaria é uma religião?', 'Não, a Maçonaria não é uma religião. Ela exige apenas que seus membros acreditem em um Ser Supremo, respeitando todas as religiões e não interferindo nas convicções religiosas pessoais.', 'faq', 'Sistema', false, 2),
('Mulheres podem ser maçons?', 'Na Maçonaria Regular, tradicionalmente, apenas homens podem ser iniciados. Existem organizações paramaçônicas para mulheres e organizações mistas, mas não são reconhecidas pela Maçonaria Regular.', 'faq', 'Sistema', false, 3),
('Qual o compromisso de tempo necessário?', 'O compromisso varia, mas geralmente inclui participação nas sessões regulares (1-2 vezes por mês), estudos e atividades da Loja. O tempo dedicado depende do envolvimento que cada membro deseja ter.', 'faq', 'Sistema', false, 4),
('Ritual do Grau de Aprendiz', 'Estudo completo sobre o ritual de iniciação no primeiro grau da Maçonaria, incluindo simbolismo e significados.', 'readings', 'Ir∴ José Mendes', true, 1),
('História da Maçonaria no Brasil', 'Análise detalhada sobre a chegada e desenvolvimento da Maçonaria no território brasileiro, desde o período colonial.', 'readings', 'Ir∴ Antonio Ferreira', false, 2),
('Filosofia Maçônica Contemporânea', 'Reflexões sobre a aplicação dos princípios maçônicos na sociedade moderna e os desafios do século XXI.', 'readings', 'Ir∴ Miguel Torres', false, 3),
('Landmarks da Maçonaria', 'Estudo sobre os marcos fundamentais e imutáveis da Ordem Maçônica e sua importância para a regularidade.', 'readings', 'Ir∴ Rafael Gomes', false, 4);